import React from 'react';
import '../Components/Style/Modale.css'; // Assurez-vous d'importer les styles nécessaires

const Modale = ({ showModal, setShowModal, loading, userData }) => {
  return (
    <div className="modal-overlay" onClick={() => setShowModal(false)}>
      <div className="modal" onClick={(e) => e.stopPropagation()}>
        <button className="close-button" onClick={() => setShowModal(false)}>
          &times;
        </button>

        {loading ? (
          <p>Chargement des données...</p>
        ) : userData ? (
          <div>
            <h2>{userData.name}</h2>
            <p><strong>Nom d'utilisateur:</strong> {userData.login}</p>
            <p><strong>Bio:</strong> {userData.bio}</p>
            <p><strong>Repositories publics:</strong> {userData.public_repos}</p>
            <p><strong>Followers:</strong> {userData.followers}</p>
            <p><strong>Following:</strong> {userData.following}</p>
            <img src={userData.avatar_url} alt="Avatar" style={{ width: '100px', borderRadius: '50%' }} />
            <a href={userData.html_url} target="_blank" rel="noopener noreferrer">
              Voir le profil GitHub
            </a>
          </div>
        ) : (
          <p>Impossible de charger les données.</p>
        )}
      </div>
    </div>
  );
};

export default Modale;
