import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import Offcanvas from 'react-bootstrap/Offcanvas';
import { NavLink } from 'react-router-dom';

function OffcanvasExample() {
  return (
    <>
      {['md'].map((expand) => (
        <Navbar key={expand} expand={expand} className="bg-dark mb-3">
          <Container fluid>
            {/* Logo visible tout le temps */}
            <div className="name text-white">John Doe</div>
            
            {/* Le bouton burger s'affiche en dessous de 768px */}
            <Navbar.Toggle aria-controls={`offcanvasNavbar-expand-${expand}`} />
            
            {/* Menu Offcanvas qui s'affiche uniquement en responsive */}
            <Navbar.Offcanvas
              id={`offcanvasNavbar-expand-${expand}`}
              aria-labelledby={`offcanvasNavbarLabel-expand-${expand}`}
              placement="end"
            >
              <Offcanvas.Header closeButton>
                <Offcanvas.Title id={`offcanvasNavbarLabel-expand-${expand}`}>
                  Menu
                </Offcanvas.Title>
              </Offcanvas.Header>
              <Offcanvas.Body>
                {/* Liens de navigation sous le logo */}
                <Nav className="flex-column">
                  <NavLink
                    to="/"
                    className={({ isActive }) =>
                      isActive ? 'nav-link-custom active' : 'nav-link-custom'
                    }
                  >
                    Accueil
                  </NavLink>
                  <NavLink
                    to="/Services"
                    className={({ isActive }) =>
                      isActive ? 'nav-link-custom active' : 'nav-link-custom'
                    }
                  >
                    Services
                  </NavLink>
                  <NavLink
                    to="/PorteFolio"
                    className={({ isActive }) =>
                      isActive ? 'nav-link-custom active' : 'nav-link-custom'
                    }
                  >
                    Portfolio
                  </NavLink>
                  <NavLink
                    to="/Contact"
                    className={({ isActive }) =>
                      isActive ? 'nav-link-custom active' : 'nav-link-custom'
                    }
                  >
                    Contact
                  </NavLink>
                  <NavLink
                    to="/MentionsLegales"
                    className={({ isActive }) =>
                      isActive ? 'nav-link-custom active' : 'nav-link-custom'
                    }
                  >
                    Mention Légales
                  </NavLink>
                </Nav>
              </Offcanvas.Body>
            </Navbar.Offcanvas>
          </Container>
        </Navbar>
      ))}
    </>
  );
}

export default OffcanvasExample;
