import React, { useState, useEffect } from 'react';
import '../Style/Home.css';
import MonImage from "../../Images/john-doe-about.jpg"; // Import de l'image locale
import ProgressBar from 'react-bootstrap/ProgressBar';
import Modale from '../Modale.js'; // Import du composant Modale

const Home = () => {
  const [showModal, setShowModal] = useState(false); // État pour gérer l'affichage de la modale
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);

  // Fonction pour récupérer les données du profil GitHub
  useEffect(() => {
    fetch('https://api.github.com/users/github-john-doe')
      .then((response) => {
        if (!response.ok) {
          throw new Error('Erreur lors de la récupération des données');
        }
        return response.json();
      })
      .then((data) => {
        setUserData(data);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Erreur:', error);
        setLoading(false);
      });
  }, []);

  return (
    <>
      <div className="ImageAccueil">
        <h1>Bonjour, je suis John Doe</h1>
        <h2>Développeur web fullstack</h2>
        <button className="btn-en-savoir-plus" onClick={() => setShowModal(true)}>
          En savoir plus
        </button>
      </div>

      <div className="row apropos">
        <section className="col-md-5">
          <h3>A propos </h3>
          <hr />
          <img className="ImageAPropos" src={MonImage} alt="John Doe" />
          <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. 
            Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. 
            Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat.
          </p>
        </section>
        <section className="col-md-5">
          <h3>Mes compétences</h3>
          <hr />
          <div>
            <h4>HTML5 90%</h4>
            <ProgressBar variant="danger" now={90} />
            <h4>CSS3 80%</h4>
            <ProgressBar variant="info" now={80} />
            <h4>JAVASCRIPT 70%</h4>
            <ProgressBar variant="warning" now={70} />
            <h4>PHP 60%</h4>
            <ProgressBar variant="success" now={60} />
            <h4>REACT 50%</h4>
            <ProgressBar variant="primary" now={50} />
          </div>
        </section>
      </div>

      {/* Affichage de la modale */}
      {showModal && (
        <Modale
          showModal={showModal}
          setShowModal={setShowModal}
          loading={loading}
          userData={userData}
        />
      )}
    </>
  );
};

export default Home;
