import React, { useState, useEffect } from 'react';
import '../Style/Home.css';
import MonImage from "../../Images/john-doe-about.jpg"; // Import de l'image locale
import ProgressBar from 'react-bootstrap/ProgressBar';

// Composant de la modale pour afficher les informations du profil GitHub
const Modal = ({ user, onClose }) => {
  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        <h2>{user.name}</h2>
        <p><strong>Bio:</strong> {user.bio}</p>
        <p><strong>Location:</strong> {user.location}</p>
        <p><strong>Repos publics:</strong> {user.public_repos}</p>
        <p><strong>Followers:</strong> {user.followers}</p>
        <p><strong>Following:</strong> {user.following}</p>
        <a href={user.html_url} target="_blank" rel="noopener noreferrer">Voir le profil GitHub</a>
        <button onClick={onClose}>Fermer</button>
      </div>
    </div>
  );
};

// Composant principal de la page d'accueil
const Home = () => {
  const [user, setUser] = useState(null); // Etat pour stocker les données de l'utilisateur GitHub
  const [isModalOpen, setIsModalOpen] = useState(false); // Etat pour gérer l'affichage de la modale

  // Utilisation de useEffect pour récupérer les données du profil GitHub lors du chargement de la page
  useEffect(() => {
    const fetchGitHubData = async () => {
      try {
        const response = await fetch('https://api.github.com/users/github-john-doe'); // URL de l'API GitHub
        const data = await response.json();
        setUser(data); // Stocke les données récupérées dans l'état `user`
      } catch (error) {
        console.error("Erreur lors de la récupération des données GitHub", error);
      }
    };
    fetchGitHubData(); // Appel de la fonction pour récupérer les données
  }, []); // Le tableau vide assure que l'effet ne s'exécute qu'une seule fois au montage du composant

  // Fonction pour ouvrir la modale
  const openModal = () => setIsModalOpen(true);

  // Fonction pour fermer la modale
  const closeModal = () => setIsModalOpen(false);

  return (
    <>
      <div className="ImageAccueil">
        <h1>Bonjour, je suis John Doe</h1>
        <h2>Développeur web fullstack</h2>
        <button className="btn-en-savoir-plus" onClick={openModal}>En savoir plus</button>
      </div>

      <div className="container">
        <section className="section1">
          <h3>A propos </h3>
          <hr />
          <img src={MonImage} alt="John Doe" />
          <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
        </section>

        <section className="section2">
          <h3>Mes compétences</h3>
          <hr />
          <div>
            <h4>HTML5 90%</h4>
            <ProgressBar variant="danger" now={90} />
            <h4>CSS3 80%</h4>
            <ProgressBar variant="info" now={80} />
            <h4>JAVASCRIPT 70%</h4>
            <ProgressBar variant="warning" now={70} />
            <h4>PHP 60%</h4>
            <ProgressBar variant="success" now={60} />
            <h4>REACT 50%</h4>
            <ProgressBar variant="primary" now={50} />
          </div>
        </section>
      </div>

      {isModalOpen && user && <Modal user={user} onClose={closeModal} />}
    </>
  );
};

export default Home;
